namespace IntegraCommerce\Api\Handler\Request\Catalog\Product;

use IntegraCommerce\Api\EntityInterface\Catalog\Product\Price;
use IntegraCommerce\Api\Handler\Request\HandlerAbstract;
use IntegraCommerce\Api\DataTransformer\Catalog\Product\Price\Update as UpdateTransformer;

class PriceHandler extends HandlerAbstract
{
    /** @var string */
    protected $baseUrlPath = '/Product';

    /**
     * @param string $sku
     * @param float $price
     * @param float $promotionalPrice
     * @param array $platformPrices
     * @return \IntegraCommerce\Api\Handler\Response\HandlerInterface
     */
    public function update(
        $sku,
        $price,
        $promotionalPrice = null,
        $platformPrices = []
    ) {
        $transformer = new UpdateTransformer(
            $price,
            $promotionalPrice,
            $platformPrices
        );

        $body = $transformer->output();

        /** @var \IntegraCommerce\Api\Handler\Response\HandlerInterface $responseHandler */
        $responseHandler = $this->service()->put($this->baseUrlPath($sku), $body);
        return $responseHandler;
    }

    /**
     * @return Price
     */
    public function entityInterface()
    {
        return new Price($this);
    }
}
